import React, { useEffect } from "react";
import BadForm from "./BadForm";
import Chaotic404 from "./Chaotic404";

function App() {

  // ðŸŽ¨ Change color randomly every 5s
  useEffect(() => {
    const changeTheme = () => {
      document.body.style.background = `hsl(${
        Math.random() * 360
      }, 100%, 70%)`;
    };
    const interval = setInterval(changeTheme, 5000);
    return () => clearInterval(interval);
  }, []);

  // ðŸŒ Slow down scroll
  useEffect(() => {
    const slowScroll = (e) => {
      window.scrollBy(0, 0.5); // slower & slower
    };
    window.addEventListener("scroll", slowScroll);
    return () => window.removeEventListener("scroll", slowScroll);
  }, []);

  return (
    <div style={{ padding: "40px" }}>
      <h1>ðŸ˜ˆ Welcome to the Website That Hates You</h1>
      <p>Everything here is designed to irritate you.</p>

      <BadForm />
      <Chaotic404 />

      <div className="popup">
        <p>You look tired. Drink water maybe?</p>
      </div>
    </div>
  );
}

export default App;
