import React, { useState } from "react";

const BadForm = () => {
  const [text, setText] = useState("");

  const handleSubmit = () => {
    alert("WRONG INPUT. Try again.");
  };

  return (
    <div style={{ marginTop: "40px" }}>
      <h3>Signup (good luck)</h3>

      <input
        type="text"
        placeholder="Enter full name but in Sanskrit"
        style={{ padding: "10px", width: "300px" }}
        value={text}
        onChange={(e) => setText(e.target.value)}
      />

      <p>Captcha: Select all images where someone is questioning life.</p>
      <img src="https://i.imgur.com/7G1Yz.png" alt="confused" width="80" />
      <img src="https://i.imgur.com/3vQ4u.png" alt="confused" width="80" />
      <img src="https://i.imgur.com/5sYeU.png" alt="confused" width="80" />

      <br />

      {/* ❌ Save & Delete Buttons – No Labels */}
      <button style={{ background: "green" }}></button>
      <button style={{ background: "red" }}></button>

      <br />
      <button onClick={handleSubmit}>Submit (Does Nothing)</button>
    </div>
  );
};

export default BadForm;
